mapper.update:
UPDATE concept SET end_date = #{endDateMarker}
WHERE `vocabulary_id` = #{vocabularyId}
AND end_date IS NULL;

UPDATE concept SET end_date = #{endDate}
WHERE `vocabulary_id` = #{vocabularyId}
AND end_date = #{endDateMarker};

mapper.insert:
INSERT INTO concept (`vocabulary_id`, `concept_code`, `concept_name`, `standard_concept`, `start_date`) VALUES

    (
    #{concept.vocabularyId},
    #{concept.conceptCode},
    #{concept.conceptName},
    #{concept.standardConcept},
    #{concept.startDate}
    )

ON DUPLICATE KEY UPDATE
`end_date`=NULL;
