mapper.update:
UPDATE concept SET end_date = #{endDateMarker}
WHERE `vocabulary_id` = #{vocabularyId}
AND end_date IS NULL;

UPDATE concept SET end_date = #{endDate}
WHERE `vocabulary_id` = #{vocabularyId}
AND end_date = #{endDateMarker};

mapper.insert:
INSERT INTO concept (`vocabulary_id`, `concept_code`, `concept_name`, `start_date`) VALUES

    (
    #{item.vocabularyId},
    #{item.conceptCode},
    #{item.conceptName},
    #{item.startDate}
    )

ON DUPLICATE KEY UPDATE
`end_date`=NULL;
