mapper.update:
UPDATE referential SET end_date = #{endDateMarker}
WHERE `type` = #{sourceType}
AND end_date IS NULL;

UPDATE referential SET end_date = #{endDate}
WHERE `type` = #{sourceType}
AND end_date = #{endDateMarker};

mapper.insert:
INSERT INTO referential (`type`, `ref_id`, `label`, `start_date`) VALUES

    (
    #{item.type},
    #{item.domainId},
    #{item.label},
    #{item.startDate}
    )

ON DUPLICATE KEY UPDATE
`end_date`=NULL;
