mapper.update:
UPDATE referential SET end_date = #{endDate}
WHERE `type` = #{sourceType}
AND end_date IS NULL;

UPDATE referential SET end_date = NOW()
WHERE `type` = #{sourceType}
AND end_date = #{endDate};

mapper.insert:
INSERT INTO referential (`type`, `ref_id`, `label`) VALUES

    (
    #{item.type},
    #{item.domainId},
    #{item.label}
    )

ON DUPLICATE KEY UPDATE
`ref_id`=VALUES (`ref_id`),
`label`=VALUES(`label`),
`end_date`=NULL;
